#!/bin/bash

# Package categories:
# System utilities
# Window manager
# Desktop environment
# Multimedia
# File managers
# Text editors
# Terminal emulator
# Application launcher
# Compositor
# Image viewer
# Screen capture
# Screen locker
# Network
# Themes and icons
# Fonts
# Programming
# Document viewer
# Archive manager

# Package list
packages=(
  # System utilities
  xorg
  sudo
  ssh
  git

  # Window manager
  openbox
  tint2
  sddm
  menu

  # Desktop environment
  lxappearance

  # Multimedia
  pulseaudio
  smplayer
  volumeicon-alsa

  # File managers
  pcmanfm
  ranger

  # Text editors
  vim
  nano

  # Terminal emulator
  xterm

  # Application launcher
  rofi

  # Compositor
  picom

  # Image viewer
  feh

  # Screen capture
  scrot

  # Screen locker
  xautolock
  slock

  # Network
  network-manager
  network-manager-gnome
  avahi-daemon
  gvfs-backends
  gvfs-fuse
  cifs-utils
  smbclient

  # Themes and icons
  numix-gtk-theme
  numix-icon-theme

  # Fonts
  fonts-firacode
  fonts-noto-cjk

  # Programming
  python3
  python3-pip

  # Document viewer
  evince

  # Archive manager
  engrampa
)

# Install packages
sudo apt update
sudo apt install -y "${packages[@]}"

if [ ! -d ~/.config ]; then
  mkdir ~/.config
fi

if [ -d ~/.config/openbox ]; then
  rm -rf ~/.config/openbox
fi

if [ -d ~/.config/tint2 ]; then
  rm -rf ~/.config/tint2
fi

echo "Copying configuration..."

openbox_tint2=""

base64 -d <<< $openbox_tint2 | tar Jxvf - -C ~/.config

cp -vf /etc/X11/xinit/xinitrc ~/.xinitrc
echo "Done"
