#!/bin/bash

# Package categories:
# System utilities
# Window manager
# Desktop environment
# Multimedia
# File managers
# Text editors
# Terminal emulator
# Application launcher
# Compositor
# Image viewer
# Screen capture
# Screen locker
# Network
# Themes and icons
# Fonts
# Programming
# Document viewer
# Archive manager

# Package list
packages=(
  # System utilities
  xorg
  sudo
  ssh
  git

  # Window manager
  openbox
  tint2
  sddm
  menu

  # Desktop environment
  lxappearance

  # Multimedia
  pulseaudio
  smplayer
  volumeicon-alsa

  # File managers
  pcmanfm
  ranger

  # Text editors
  vim
  nano

  # Terminal emulator
  xterm

  # Application launcher
  rofi

  # Compositor
  picom

  # Image viewer
  feh
  mirage

  # Screen capture
  scrot

  # Screen locker
  xautolock
  slock

  # Network
  network-manager
  network-manager-gnome
  avahi-daemon
  gvfs-backends
  gvfs-fuse
  cifs-utils
  smbclient

  # Themes and icons
  numix-gtk-theme
  numix-icon-theme

  # Fonts
  fonts-firacode
  fonts-noto-cjk

  # Programming
  python3
  python3-pip

  # Document viewer
  evince

  # Archive manager
  engrampa
)

# Install packages
sudo apt update
sudo apt install -y "${packages[@]}"

if [ ! -d ~/.config ]; then
  mkdir ~/.config
fi

if [ -d ~/.config/openbox ]; then
  rm -rf ~/.config/openbox
fi

if [ -d ~/.config/tint2 ]; then
  rm -rf ~/.config/tint2
fi

if [ -d ~/.config/picom ]; then
  rm -rf ~/.config/picom
fi

if [ -d ~/.local/share/applications ]; then
  if [ -f ~/.local/share/applications/xterm.desktop ]; then
    rm -f ~/.local/share/applications/xterm.desktop
  fi
else
  mkdir -p ~/.local/share/applications
fi

if [ -d ~/.local/share/icons ]; then
  if [ -f ~/.local/share/icons/xterm.png ]; then 
    rm -f ~/.local/share/icons/xterm.png
  fi
else
  mkdir -p ~/.local/share/icons
fi

xterm_png=""
base64 -d <<< $xterm_png | xz -d > ~/.local/share/icons/xterm.png

cat > ~/.local/share/applications/xterm.desktop << EOF
[Desktop Entry]
Name=Xterm
Comment=Terminal Emulator
Exec=/usr/bin/xterm
Icon=/usr/share/icons/gnome/256x256/apps/utilities-terminal.png
Terminal=false
Type=Application
Categories=Utility;TerminalEmulator;
EOF

echo "Copying configuration..."

openbox_tint2=""

base64 -d <<< $openbox_tint2 | tar Jxvf - -C ~/.config

cp -vf /etc/X11/xinit/xinitrc ~/.xinitrc
echo "Done"
