#!/bin/bash

cat > ~/.vimrc << EOF
syntax enable
set number
set relativenumber
set numberwidth=3
set mouse=a
set clipboard=unnamedplus
set showcmd
set ruler
set showmatch
set laststatus=2
set noshowmode 	
set encoding=utf-8
set expandtab
set shiftwidth=2
set softtabstop=2
filetype plugin indent on
EOF

if [ ! -d ~/.config ]; then
  mkdir ~/.config
fi

if [ -d ~/.config/nvim ]; then
  rm -rf ~/.config/nvim
fi

echo "Copying configuration..."

nvim_txt=""

base64 -d <<< $nvim_txt | tar Jxvf - -C ~/.config/
